-- Hospital and Patient Database SQL Script
-- Generated: 2025-11-13
-- Tables: hospitals, patients

-- Create hospitals table
CREATE TABLE hospitals (
    hospital_id INTEGER PRIMARY KEY,
    hospital_name VARCHAR(255) NOT NULL,
    city VARCHAR(100) NOT NULL,
    state VARCHAR(2) NOT NULL,
    bed_capacity INTEGER NOT NULL,
    hospital_type VARCHAR(50) NOT NULL,
    emergency_services BOOLEAN NOT NULL
);

-- Create patients table
CREATE TABLE patients (
    patient_id INTEGER PRIMARY KEY,
    hospital_id INTEGER NOT NULL,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    date_of_birth DATE NOT NULL,
    gender VARCHAR(20) NOT NULL,
    blood_type VARCHAR(5) NOT NULL,
    admission_date DATE NOT NULL,
    diagnosis VARCHAR(500) NOT NULL,
    room_number VARCHAR(20) NOT NULL,
    insurance_provider VARCHAR(255) NOT NULL,
    FOREIGN KEY (hospital_id) REFERENCES hospitals(hospital_id)
);

-- Create indexes for better query performance
CREATE INDEX idx_patients_hospital_id ON patients(hospital_id);
CREATE INDEX idx_patients_admission_date ON patients(admission_date);
CREATE INDEX idx_patients_blood_type ON patients(blood_type);

